#Q1
def how_many(journalists)
    print ">"
    puts "Dans cette base de donnée, il y a #{journalists.length} handles."
end

#Q2
def shortest_name(journalists)
    mini = journalists.min {|a, b| a.length <=> b.length}
    print ">"
    puts "L'handle le plus court est #{mini}."
end

#Q3
def five_characters(journalists)
    names_fivec = journalists.select {|word| word.length ==6}.length
    print ">"
    puts "Nous avons #{names_fivec} handles ayant cinq caractères."
end

#Q4
def uppercase(journalists)
    first_letter_uppercase = journalists.select{|journalists| journalists.start_with?(/@[[:upper:]]/)}
    first_letter = first_letter_uppercase.count
    print ">"
    puts "Il y a #{first_letter} handles dont la première lettre est une majucule."
end

#Q5
def alpha_order(journalists)
    order = journalists.sort_by(&:downcase)
    puts "> Voici la liste des handles triée par ordre alphabétique #{order}."
end

#Q6
def order_small_big(journalists)
small_to_big = journalists.sort{|word| word.length}
print ">"
puts "L'ordre des noms classés du plus petits au plus grands est #{small_to_big}"
end

#Q7 
def epenser(journalists)
    one_name = journalists.index ("@epenser")
    print ">"
    puts "La position de epenser est #{one_name}"
end

#Q8 - pris à un membre de mon équipe
def length_frequency(journalists)     
    handle_length_frequency = Hash.new(0)
    for i in 0...journalists.length    
      handle_length_frequency[journalists[i].length] +=  1
    end
    puts "Souhaites tu une répartition des handles de la base de données en fonction de leurs nombre de caractères ? Cliquez sur la touche entrer pour initiez la requête."
    initiate = gets.chomp
    a = 1
    for i in 1..33
      if a == 1 or a == 2 or a == 3 or a == 17 or a == 18 or a == 19 or a == 20 or a == 21  or a == 22 or a == 23 or a == 24 or a == 25 or a == 26 or a == 27 or a == 28 or a == 29 or a == 30 or a == 31 or a == 32
        puts "Il y a #{handle_length_frequency.default} handles Twitter avec #{i} caractères"
      else 
        puts "Il y a #{handle_length_frequency.fetch(a)} handles Twitter avec #{i} caractères"
      end
      a += 1
      i += 1
    end
  end 

def perform
    how_many(data)
    shortest_name(data)
    five_characters(data)
    uppercase(data)
    alpha_order(data)
    order_small_big(data)
    epenser(data)
    length_frequency(data)
end
